div.style:
width: 32px;
height: 32px;
box-shadow:
  inset 2px 2px 6px rgba(255, 255, 255, 0.2),
  inset -2px -2px 6px rgba(0, 0, 0, 0.8);
border: 4px ridge pink;
border-radius: 100%;
background:
  linear-gradient(black, black) padding-box,
  repeating-conic-gradient(#222 0deg 30deg, #111 30deg 60deg) border-box;
aspect-ratio: 1/1;
overflow: hidden;
box-sizing: border-box;
